<?php

/**
 * CentrosTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class CentrosTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object CentrosTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Centros');
    }

    // Obtener obras sociales
    public static function obtenerCentrosActivos($estado=NULL)
    {
        $sql ="select id, activo, descripcion, abreviacion FROM centros WHERE 1 = 1 ";

        if($estado !== NULL)
            $sql .=  " AND activo = ".$estado." "; 

        $sql .= " ORDER BY descripcion;";
        
        $q = Doctrine_Manager::getInstance()->getCurrentConnection()->fetchAssoc($sql);

        return $q;
    } 
}