<?php

/**
 * PacienteTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class PacienteTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object PacienteTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Paciente');
    }
    // Busca el paciente por dni
    public static function getPacienteIdByNroDoc($criterio) {
		$q = Doctrine_Query::create()
	  		->select("*")
	 		->from("Paciente p")
	 		->where("p.nrodoc = ".$criterio)
	    	->fetchOne();
	       
        return $q;
    }

      // Obtener obras sociales
    public static function obtenerPacientesActivos($estado=NULL)
    {
        $sql ="SELECT id, apellido, nombre FROM paciente WHERE 1 = 1 ";
      //  if($estado !== NULL)
        //    $sql .=  " AND activo = ".$estado." "; 
        $sql .= " ORDER BY apellido;";
        
        $q = Doctrine_Manager::getInstance()->getCurrentConnection()->fetchAssoc($sql);
        return $q;
    } 
}